import type { FC } from 'react';
import React, { useState, useEffect } from 'react';
import { useIntl } from 'umi';
import { Slider, InputNumber, Image } from 'antd';
import { getPaletteFromImage } from '@antv/smart-color';
import type { Palette } from '@antv/color-schema';
import styles from './index.less';
import Swatch from '@/components/Swatch';

interface PaletteByImageProps {
  image: string;
  save?: (palette: Palette) => void;
}

const PaletteByImage: FC<PaletteByImageProps> = ({ image, save }) => {
  const { formatMessage } = useIntl();
  const [colorCount, setColorCount] = useState<number>(6);
  const [isError, setIsError] = useState<boolean>(false);
  const [palette, setPalette] = useState<Palette>();

  const onChange = (value: any) => {
    setColorCount(typeof +value === 'number' ? +value : 0);
  };

  useEffect(() => {
    async function getPalette() {
      await getPaletteFromImage(image, colorCount)
        .then((result) => {
          if (result) {
            setPalette(result);
          }
        })
        .catch(() => {
          setPalette(undefined);
        });
    }
    getPalette();
  }, [image, colorCount]);

  useEffect(() => {
    if (save && palette) {
      save(palette);
    }
  }, [save]);

  return (
    <>
      <Image
        width={'100%'}
        src={image}
        preview={false}
        fallback="data:image/png;base64,"
        onError={() => setIsError(true)}
      />
      {!isError && (
        <>
          <div className={styles.colorNumber}>
            <span>{formatMessage({ id: 'Color Number' })}</span>
            <Slider min={1} max={20} onChange={onChange} value={colorCount} className={styles.colorNumberSlider} />
            <InputNumber
              size="small"
              min={1}
              max={20}
              value={colorCount}
              onChange={onChange}
              className={styles.colorNumberInput}
            />
          </div>
          {palette && <Swatch palette={palette} toolbar={false} />}
        </>
      )}
    </>
  );
};

export default PaletteByImage;
